package kz.ncanode.common

import kz.gov.pki.kalkan.jce.provider.KalkanProvider
import kz.gov.pki.kalkan.xmldsig.KncaXS
import kz.ncanode.constants.MessageConstants
import org.apache.http.HttpEntity
import org.apache.http.StatusLine
import org.apache.http.client.methods.CloseableHttpResponse

import java.security.Security

import static org.mockito.Mockito.doReturn
import static org.mockito.Mockito.mock

interface WithTestData {

    /**
     * Невалидный ключ, с невалидным Base64
     */
    final static String KEY_INVALID_BASE64 = 'invalid?base64&@#$'

    /**
     * Невалидный ключ, с валидным Base64
     */
    final static String KEY_INVALID = 'aW52YWxpZD9iYXNlNjQmQCMk'

    /**
     * Неверный пароль
     */
    final static String KEY_INVALID_PASSWORD = 'invalidpassword000'

    /**
     * Неверный алиас
     */
    final static String KEY_INVALID_ALIAS = 'invalidalias000'

    /**
     * Сообщение для неверного алиаса
     */
    final static String KEY_INVALID_ALIAS_MESSAGE = String.format(MessageConstants.KEY_ALIAS_NOT_FOUND, KEY_INVALID_ALIAS)

    /**
     * Ключ для физ.лиц, действующий, для подписи, старый (2004)
     */
    final static String KEY_INDIVIDUAL_VALID_SIGN_2004 = ''
    final static String KEY_INDIVIDUAL_VALID_SIGN_2004_PASSWORD = 'Qwerty12'
    final static String KEY_INDIVIDUAL_VALID_SIGN_2004_ALIAS = 'e3fe35adda3b45cbea3a3f1ed48f263dc55c556e'

    /**
     * Ключ для физ.лиц, действующий, новый (2015)
     */
    final static String KEY_INDIVIDUAL_VALID_2015 = ''
    final static String KEY_INDIVIDUAL_VALID_2015_PASSWORD = 'Aa123456'
    final static String KEY_INDIVIDUAL_VALID_2015_ALIAS = '8ff06211dc82beb00253ffa000f1de84abef454f'

    /**
     * Ключ для физ лиц, отозванный
     */
    final static String KEY_INDIVIDUAL_AUTH_REVOKED_2004 = ''
    final static String KEY_INDIVIDUAL_SIGN_REVOKED_2004 = ''

    /**
     * Ключ Первого руководителя отзванный
     */
    final static String KEY_CEO_SIGN_REVOKED_2004 = ''

    /**
     * Ключ физ лица отозванный 2015
     */
    final static String KEY_CEO_REVOKED_2015 = ''

    /**
     * Референсы
     */
    final static String REFERENCE_URI = 'testreference1'

    /**
     * Тестовые сертификаты НУЦ
     */
    final static String NCA_2004_GOST_CERT = ''
    final static String NCA_2004_RSA_CERT = ''
    final static String NCA_2015_CERT = ''

    /**
     * Сертификат от ЭЦП
     */
    final static String CERT_INDIVIDUAL = ''

    default CloseableHttpResponse createMockedResponse(InputStream inputStream, Integer statusCode = 200) {
        def entity = mock(HttpEntity)
        doReturn(inputStream).when(entity).getContent()

        def statusLine = mock(StatusLine)
        doReturn(statusCode).when(statusLine).getStatusCode()

        def response = mock(CloseableHttpResponse)
        doReturn(entity).when(response).getEntity()
        doReturn(statusLine).when(response).getStatusLine()

        return response
    }

    default void initializeKalkanLibrary() {
        Security.addProvider(new KalkanProvider());
        KncaXS.loadXMLSecurity();
    }
}
